// jszip.min.js (local loader)
// This small loader dynamically loads JSZip from CDN so that the "download all ZIP" feature works.
// If you need fully offline operation, replace this file with the actual jszip.min.js content.
(function(){
  if(window.JSZip) return;
  var s = document.createElement('script');
  s.src = 'https://cdnjs.cloudflare.com/ajax/libs/jszip/3.10.1/jszip.min.js';
  s.onload = function(){ console.log('JSZip loaded from CDN'); };
  s.onerror = function(){ console.warn('Failed to load JSZip from CDN. For fully offline use, place jszip.min.js in assets/js/'); };
  document.head.appendChild(s);
})();
